def getDescentPeriods(prices):
    c=len(prices)
    d = ()
    i = 0
    while i < len(prices):
        j=1
        while j < len(prices)-1:
            if i+j not in d:
                l = prices[i:i+j+1]
                if len([True for x in range(len(l)-1) if l[x]==l[x+1]+1]) == j:
                    c+= j
                    d = d + (i+j, )
            j=j+1
        i=i+1
    return c
